mapper.select:
select sum(stock_quantity) from stock


        stock_date = #{day}
        and shop_id in

            #{shopId}

SELECT t.price, SUM(s.stock_quantity) AS total_quantity FROM stock s JOIN terminal t ON s.terminal_id = t.terminal_id WHERE s.shop_id IN
#{shopId}
AND s.stock_date BETWEEN #{begin} AND #{end} GROUP BY t.price
